makefile.post:
.PHONY: makefile
makefile:
	../tools/makefile.sh  
files:
	@java -jar ${TOOLS}/projects.jar files ${_dp_PROJECTS_XML} ${_dp_HOME} | sort
all:	${_dp_PRJ_LIST}
clean:	
	rm -f ${_dp_MOD_LIST} ${_dp_PRJ_LIST}
namespace:
	mkdir -p ${_dp_NAMESPACE}

#general rules
# all depends on sys/sys.h
${_dp_MOD_LIST}: sys/sys.h
